import React, { useEffect, useState } from 'react';
import Navbar from '../Navbar/Navbar';
import './Notification.css'; // Import the stylesheet

// Function component Notification to display user notifications
const Notification = ({ children }) => {
  // State variables
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState("");
  const [doctorData, setDoctorData] = useState(null);
  const [appointmentData, setAppointmentData] = useState(null);
  const [showNotification, setShowNotification] = useState(true);

  // useEffect to fetch data from storage
  useEffect(() => {
    const storedUsername = sessionStorage.getItem('email');
    const storedDoctorData = JSON.parse(localStorage.getItem('doctorData'));
    const storedAppointmentData = JSON.parse(localStorage.getItem(storedDoctorData?.name));

    if (storedUsername) {
      setIsLoggedIn(true);
      setUsername(storedUsername);
    }

    if (storedDoctorData) {
      setDoctorData(storedDoctorData);
    }
x
    if (storedAppointmentData) {
      setAppointmentData(storedAppointmentData);
    }
  }, []);

  // ✅ Effect to handle appointment cancellation
  useEffect(() => {
    const handleAppointmentCancel = () => {
      // Example: when appointment data is removed from localStorage
      const storedDoctorData = JSON.parse(localStorage.getItem('doctorData'));
      const checkAppointment = JSON.parse(localStorage.getItem(storedDoctorData?.name));
      if (!checkAppointment) {
        setShowNotification(false); // Hide notification
      }
    };

    window.addEventListener('storage', handleAppointmentCancel); // listen for changes
    return () => window.removeEventListener('storage', handleAppointmentCancel);
  }, []);

  const handleCancel = () => {
    localStorage.removeItem(doctorData?.name);
    setShowNotification(false);
  };

  return (
    <div>
      <Navbar />
      {children}

      {isLoggedIn && appointmentData && showNotification && (
        <div className="notification-container">
          <div className="appointment-card">
            <div className="appointment-card__content">
              <h3 className="appointment-card__title">Appointment Details</h3>
              <p><strong>Patient:</strong> {username}</p>
              <p><strong>Doctor:</strong> {doctorData?.name}</p>
              <p><strong>Date:</strong> {appointmentData?.date || 'Not specified'}</p>
              <p><strong>Time:</strong> {appointmentData?.time || 'Not specified'}</p>
              <button className="cancel-btn" onClick={handleCancel}>Cancel Appointment</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Notification;
