import React, { useState, useEffect } from "react";
import "./ReviewForm.css";

const ReviewForm = () => {
  const [appointments, setAppointments] = useState([]); // instead of doctors
  const [selectedAppointment, setSelectedAppointment] = useState(null);
  const [formData, setFormData] = useState({ name: "", review: "", rating: "" });
  const [submittedReviews, setSubmittedReviews] = useState({});

  useEffect(() => {
    const getDoctorsDetails = async () => {
      try {
        const res = await fetch("https://api.npoint.io/9a5543d36f1460da2f63");
        const data = await res.json();

        const simulatedAppointments = data.flatMap((doctor) =>
          [1, 2].map((num) => ({
            id: `${doctor.id || doctor.name}-${num}`,
            doctorName: doctor.name,
            speciality: doctor.speciality,
            appointmentNumber: num,
          }))
        );

        setAppointments(simulatedAppointments);
      } catch (err) {
        console.error("Error fetching doctors:", err);
      }
    };
    getDoctorsDetails();
  }, []);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  // ✅ Handle form submit
  const handleSubmit = (e) => {
    e.preventDefault();

    if (!formData.name || !formData.review || !formData.rating) {
      alert("⚠️ Please fill in all fields.");
      return;
    }

    setSubmittedReviews((prev) => ({
      ...prev,
      [selectedAppointment.id]: { ...formData },
    }));

    setFormData({ name: "", review: "", rating: "" });
    setSelectedAppointment(null);
  };

  // Close modal when clicking outside
  const handleOutsideClick = (e) => {
    if (e.target.classList.contains("modal-overlay")) {
      setSelectedAppointment(null);
    }
  };

  return (
    <div className="review-table-container">
      <h2>Doctor Reviews</h2>
      <table className="review-table">
        <thead>
          <tr>
            <th>Serial No.</th>
            <th>Doctor Name</th>
            <th>Speciality</th>
            <th>Appointment</th>
            <th>Provide Feedback</th>
            <th>Review Given</th>
          </tr>
        </thead>
        <tbody>
          {appointments.map((appt, index) => (
            <tr key={appt.id}>
              <td>{index + 1}</td>
              <td>{appt.doctorName}</td>
              <td>{appt.speciality}</td>
              <td>#{appt.appointmentNumber}</td>
              <td>
                <button
                  onClick={() => setSelectedAppointment(appt)}
                  disabled={submittedReviews[appt.id]}
                  className="feedback-btn"
                >
                  {submittedReviews[appt.id]
                    ? "Feedback Submitted"
                    : "Click Here"}
                </button>
              </td>
              <td>
                {submittedReviews[appt.id] ? (
                  <>
                    <strong>{submittedReviews[appt.id].name}</strong>:{" "}
                    {submittedReviews[appt.id].review} ⭐
                    {submittedReviews[appt.id].rating}
                  </>
                ) : (
                  "-"
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Modal popup for feedback */}
      {selectedAppointment && (
        <div className="modal-overlay" onClick={handleOutsideClick}>
          <div className="modal-content">
            <h3>
              Leave Feedback for {selectedAppointment.doctorName} (Appointment #
              {selectedAppointment.appointmentNumber})
            </h3>
            <form onSubmit={handleSubmit}>
              <label>
                Your Name:
                <input
                  type="text"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
                  required
                />
              </label>

              <label>
                Your Review:
                <textarea
                  name="review"
                  value={formData.review}
                  onChange={handleChange}
                  required
                />
              </label>

              <label>
                Rating (1–5):
                <select
                  name="rating"
                  value={formData.rating}
                  onChange={handleChange}
                  required
                >
                  <option value="">Select</option>
                  {[1, 2, 3, 4, 5].map((num) => (
                    <option key={num} value={num}>
                      {num}
                    </option>
                  ))}
                </select>
              </label>

              <div className="form-buttons">
                <button type="submit">Submit</button>
                <button
                  type="button"
                  onClick={() => setSelectedAppointment(null)}
                  className="cancel-btn"
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default ReviewForm;
