import React, { useEffect, useState } from "react";
import "./ReviewForm.css";

const ReviewForm = () => {
  const [doctors, setDoctors] = useState([]); // store doctors from API
  const [feedbacks, setFeedbacks] = useState({}); // { doctorName: "feedback text" }
  const [selectedDoctor, setSelectedDoctor] = useState(null);
  const [reviewText, setReviewText] = useState("");

  // Fetch doctor data (same API as BookingConsultation.js)
  useEffect(() => {
    const getDoctorsDetails = async () => {
      try {
        const res = await fetch("https://api.npoint.io/9a5543d36f1460da2f63");
        const data = await res.json();
        setDoctors(data);
      } catch (err) {
        console.error("Error fetching doctors:", err);
      }
    };
    getDoctorsDetails();
  }, []);

  const handleFeedbackClick = (doctor) => {
    setSelectedDoctor(doctor);
    setReviewText(feedbacks[doctor.name] || "");
  };

  const handleSubmitFeedback = (e) => {
    e.preventDefault();
    if (!reviewText.trim()) return;

    setFeedbacks({
      ...feedbacks,
      [selectedDoctor.name]: reviewText,
    });

    setSelectedDoctor(null);
    setReviewText("");
  };

  return (
    <div className="review-container">
      <h2>Doctor Reviews</h2>

      {/* Doctor table */}
      <table className="doctor-table">
        <thead>
          <tr>
            <th>No.</th>
            <th>Doctor Name</th>
            <th>Speciality</th>
            <th>Provide Feedback</th>
            <th>Review Given</th>
          </tr>
        </thead>
        <tbody>
          {doctors.length > 0 ? (
            doctors.map((doctor, index) => (
              <tr key={doctor.id}>
                <td>{index + 1}</td>
                <td>{doctor.name}</td>
                <td>{doctor.speciality}</td>
                <td>
                  <button
                    className="feedback-btn"
                    onClick={() => handleFeedbackClick(doctor)}
                  >
                    Click here
                  </button>
                </td>
                <td>{feedbacks[doctor.name] || "No review yet"}</td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="5">Loading doctors...</td>
            </tr>
          )}
        </tbody>
      </table>

      {/* Feedback form modal */}
      {selectedDoctor && (
        <div className="feedback-modal">
          <div className="feedback-content">
            <h3>Provide Feedback for {selectedDoctor.name}</h3>
            <form onSubmit={handleSubmitFeedback}>
              <textarea
                value={reviewText}
                onChange={(e) => setReviewText(e.target.value)}
                placeholder="Write your feedback here"
                required
              />
              <div className="modal-actions">
                <button type="submit" className="submit-btn">
                  Submit
                </button>
                <button
                  type="button"
                  className="cancel-btn"
                  onClick={() => setSelectedDoctor(null)}
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default ReviewForm;
